import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import logo from '../images/logo.png';
import '../css/app.css';
import { withGetScreen } from 'react-getscreen';


const btnStyle = {
	textAlign: 'center',
	marginLeft: '10px'
};

class Header extends Component {
	componentDidMount() {
		window.addEventListener('scroll', this.resizeHeaderOnScroll);
	}
	resizeHeaderOnScroll() {
		const distanceY = window.pageYOffset || document.documentElement.scrollTop,
			shrinkOn = 200,
			headerEl = document.getElementById('js-header');

		//window.innerWidth
		if (distanceY > shrinkOn && window.innerWidth > 700) {
			headerEl.classList.add('smaller');
		} else {
			headerEl.classList.remove('smaller');
		}
	}

	renderContent() {
		switch (this.props.auth) {
			case null:
				console.log('auth null');
				return;
			case false:
					console.log('auth false');
				return [
					<li key="1"><a className="btn blue darken-1" href="/login">Login</a></li>,
					<li key="2"><a className="btn blue darken-1" href="/signup">Sign Up</a></li>
				];
			default:
					console.log('auth default');
				return (
					<li><a className="btn blue darken-1" href="/api/logout">Logout</a></li>
				);
		}
	}

	render() {
		return (
			<header id="js-header">
				<div className="nav-wrapper">
					<Link to="/">
						<img id="logo" src={logo} />
					</Link>
					<navbar>
						<Link to="/">Home</Link>
						<Link to="/Services">Services</Link>
						<Link to="/ContactUs">Contact Us</Link>
						<Link to="/AboutUs">About Us</Link>
						{this.renderContent()}
					</navbar>
				</div>
			</header>
		);
	}
}

function mapStateToProps({ auth }) {
	return { auth };
}

export default connect(mapStateToProps)(Header);
