import React from 'react';
import img1 from '../../images/photo1.jpg';
import img2 from '../../images/photo2.jpg';
import img3 from '../../images/photo3.jpeg';

const imgStyle = {
    display: 'block',
    margin: '0 auto',
    width: '50%',
    height: 'auto',
    borderRadius: '50%',
    align: 'center',
};

function getImage({key}) {
    switch(key) {
        case 1: return img1;
        case 2: return img2;
        default: return img3;
    }
}  

export default ({ input, key, label, cImage, cText}) => {


	return (
		<div className="col s4">
				<div className="card teal white-text" key={key}>
                    <div className="card-image">
                        <img style={imgStyle} src={getImage({key})} />
                    </div>
					<div className="card-content">
						<span className="card-title">{label}</span>
						<p>{cText}</p>
					</div>
				</div>
                </div>
	);
};
